            thumbnail: "/assets/img/demo/coil-notebook.png",
            class: "card-coil-notebook",
            code: "vitesse-dark",
            css: '/* 颜色变量定义 */
.card-coil-notebook {
  /* 基础颜色 */
  --coil-text: #24292e;
  --coil-border: #a6a6a6;

  /* 背景模式变量 */
  --coil-bg-color: #5271ff;
  --coil-grid-color: rgba(255, 255, 255, 0.2);
  --coil-content-bg: #fff;
  --coil-list-bg: rgba(255, 255, 255, 0.7);
  --coil-shadow: rgba(0, 0, 0, 0.1);
}

/* 小红书粉色主题 */
.card-coil-notebook.pink-mode {
  --coil-bg-color: #ff4e79;
}

/* 小红书薄荷绿主题 */
.card-coil-notebook.mint-mode {
  --coil-bg-color: #7fd1ae;
}

/* 小红书暖黄主题 */
.card-coil-notebook.yellow-mode {
  --coil-bg-color: #ffd666;
  --coil-grid-color: rgba(0, 0, 0, 0.1);
  --coil-list-bg: rgba(255, 255, 255, 0.9);
}

/* 线圈笔记本样式 */
.card-coil-notebook {
  background-color: var(--coil-bg-color) !important;
  background-image:
    linear-gradient(var(--coil-grid-color) 1px, transparent 1px),
    linear-gradient(90deg, var(--coil-grid-color) 1px, transparent 1px);
  background-size: 20px 20px;
  background-position: -5px -5px;
  padding: 20px;
}

.card-content {
  background-image: url("/assets/img/coil-bg.png");
  background-position: top left;
  background-repeat: repeat-y;
  background-size: 36px 33px;
  padding-left: 36px;
  box-shadow: 0 2px 10px var(--coil-shadow);
}
.card-content-inner {
  height: calc(var(--card-height) - 40px);
  background: var(--coil-content-bg);
  padding: 20px 20px 20px 0;
}

/* 列表样式 */
.card-content ul,
.card-content ol {
  background-color: var(--coil-list-bg);
  padding: 15px;
  border: 1px dashed var(--coil-border);
  border-radius: 8px;
}
.card-content ul li {
  margin: 10px 0;
}
.card-content ol li::before,
.card-content ul li::before {
  background: #ffd666;
  margin-right: 8px;
  display: block;
  float: left;
}
.card-content ul li::before {
  content: "★";
  color: var(--coil-text);
  font-size: 14px;
  background-size: 24px;
  background-repeat: no-repeat;
  background-position: center;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  text-align: center;
  line-height: 24px;
}
.card-content ol li {
  margin: 10px 0;
  counter-increment: item;
}

.card-content ol li::before {
  content: attr(data-index);
  background-size: 24px;
  background-repeat: no-repeat;
  background-position: center;
  color: var(--coil-text);
  font-weight: 700;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  text-align: center;
  line-height: 24px;
}

/* 响应式调整 */
@media (prefers-color-scheme: dark) {
  .card-coil-notebook {
    --coil-grid-color: rgba(255, 255, 255, 0.15);
    --coil-content-bg: #ffffff;
    --coil-list-bg: rgba(255, 255, 255, 0.8);
    --coil-shadow: rgba(0, 0, 0, 0.2);
  }
}

.card-coil-notebook .card-footer {
  position: relative;
}

.card-coil-notebook .card-footer .page {
  font-size: 12px;
  position: absolute;
  right: 10px;
  bottom: 10px;
}
