  font-family: "Rajdhani", "Orbitron", "PingFang SC", sans-serif;
  position: relative;
  box-shadow:
    0 0 25px rgba(0, 255, 255, 0.2),
    0 0 5px rgba(255, 0, 128, 0.15);
  color: #e0e0e0;
}

/* 背景网格效果 */
.card-cyberpunk:before {
  width: 100%;
  height: 100%;
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background:
    linear-gradient(rgba(18, 16, 36, 0.8) 1px, transparent 1px),
    linear-gradient(90deg, rgba(18, 16, 36, 0.8) 1px, transparent 1px);
  background-size: 20px 20px;
  z-index: 1;
  opacity: 0.2;
}

/* 故障艺术装饰 */
.card-cyberpunk:after {
  content: "";
  position: absolute;
  top: 0;
  left: -10%;
  width: 120%;
  height: 10px;
  background: linear-gradient(
    90deg,
    transparent 5%,
    rgba(0, 255, 255, 0.8) 15%,
    rgba(255, 0, 128, 0.8) 25%,
    transparent 35%
  );
  opacity: 0.7;
  filter: blur(1px);
  transform: skewX(-20deg);
  /* animation: glitch 10s infinite linear; */
  z-index: 2;
}

@keyframes glitch {
  0% {
    top: -10px;
    opacity: 0.7;
  }
  20% {
    top: 40px;
    opacity: 0.4;
  }
  40% {
    top: 80px;
    opacity: 0.6;
  }
  60% {
    top: 120px;
    opacity: 0.3;
  }
  80% {
    top: 180px;
    opacity: 0.5;
  }
  100% {
    top: 240px;
    opacity: 0;
  }
}

.card-cyberpunk .card-content-inner {
  position: relative;
  z-index: 3;
}

.card-cyberpunk .card-content {
  position: relative;
  background: rgba(13, 14, 25, 0.7);
  padding: 20px;
  height: calc(100% - 71px);
  border: 1px solid rgba(0, 255, 255, 0.2);
  box-shadow: 0 0 10px rgba(0, 255, 255, 0.1) inset;
}

/* 主标题样式 - 赛博朋克风格 */
.card-cyberpunk h1 {
  font-family: "Orbitron", "PingFang SC", sans-serif;
  font-size: 28px;
  position: relative;
  color: #fff;
  margin: 0 0 25px 0;
  font-weight: 700;
  text-align: left;

  padding: 0 0 10px 0;
  text-shadow:
    0 0 5px rgba(0, 255, 255, 0.7),
    0 0 10px rgba(0, 255, 255, 0.5);
  border-bottom: 2px solid rgba(255, 0, 128, 0.5);
}

/* 标题故障效果 */
.card-cyberpunk h1:before {
  content: attr(data-text);
  position: absolute;
  left: -2px;
  top: 0;
  color: rgba(255, 0, 128, 0.8);
  overflow: hidden;
  clip-path: rect(20px 990px 40px 0px);
  text-shadow: none;
  opacity: 0.8;
}

/* 副标题样式 */
.card-cyberpunk h2 {
  font-size: 18px;
  color: #00ffff;
  margin: 25px 0 15px 0;
  font-weight: 600;
  text-align: left;
  position: relative;

  padding-left: 15px;
  border-left: 3px solid rgba(255, 0, 128, 0.7);
}

/* 段落样式 */
.card-cyberpunk p {
  color: #e0e0e0;
  font-size: 15px;
  text-align: justify;
  font-family: "Rajdhani", "PingFang SC", sans-serif;
}

.card-cyberpunk strong {
  color: #ff0080;
  font-weight: 700;
  text-shadow: 0 0 3px rgba(255, 0, 128, 0.5);
}

.card-cyberpunk em {
  color: #00ffff;
  font-style: italic;
  text-shadow: 0 0 3px rgba(0, 255, 255, 0.5);
}

.card-cyberpunk p a {
  color: #00ffff;
  text-decoration: none;
  border-bottom: 1px solid rgba(0, 255, 255, 0.3);
  transition: all 0.2s ease;
  position: relative;
  padding: 0 3px;
}

.card-cyberpunk p a:hover {
  background: rgba(0, 255, 255, 0.1);
  border-bottom: 1px solid rgba(0, 255, 255, 0.7);
}

.card-cyberpunk p a:before {
  content: "";
  position: absolute;
  bottom: -2px;
  left: 0;
  width: 100%;
  height: 1px;
  background: rgba(0, 255, 255, 0.5);
  animation: link-glitch 2s infinite;
}

@keyframes link-glitch {
  0% {
    transform: translateX(0);
  }
  80% {
    transform: translateX(0);
  }
  85% {
    transform: translateX(-3px);
  }
  90% {
    transform: translateX(3px);
  }
  95% {
    transform: translateX(-2px);
  }
  100% {
    transform: translateX(0);
  }
}

/* 有序列表样式 */
.card-cyberpunk li {
  position: relative;
  counter-increment: item;
  list-style: none;
  margin-bottom: 15px;
  padding-left: 40px;
  color: #e0e0e0;
  line-height: 1.6;
  font-size: 15px;
  font-family: "Rajdhani", "PingFang SC", sans-serif;
}

/* 序号赛博朋克风格 */
.card-cyberpunk li:before {
  content: attr(data-index);
  position: absolute;
  left: 0;
  top: -3px;
  width: 28px;
  height: 28px;
  background: rgba(255, 0, 128, 0.2);
  color: #ff0080;
  text-align: center;
  line-height: 28px;
  font-weight: bold;
  font-size: 14px;
  border: 1px solid rgba(255, 0, 128, 0.5);
  box-shadow: 0 0 5px rgba(255, 0, 128, 0.3);
}

/* 小标题样式 */
.card-cyberpunk h3 {
  display: block;
  color: #00ffff;
  margin: 20px 0 10px 0;
  font-size: 16px;
  font-weight: 700;

  text-shadow: 0 0 3px rgba(0, 255, 255, 0.5);
}

/* 表格样式 */
.card-cyberpunk table {
  border-collapse: collapse;
  margin: 20px 0;
  width: 100%;
  border: 1px solid rgba(0, 255, 255, 0.3);
  background: rgba(13, 14, 25, 0.5);
}

.card-cyberpunk th {
  background-color: rgba(0, 255, 255, 0.1);
  color: #00ffff;
  padding: 10px;
  text-align: left;
  font-weight: 600;
  font-size: 14px;
  letter-spacing: 1px;
  border: 1px solid rgba(0, 255, 255, 0.3);
}

.card-cyberpunk td {
  color: #e0e0e0;
  padding: 10px;
  border: 1px solid rgba(0, 255, 255, 0.2);
}

.card-cyberpunk tr:nth-child(even) {
  background-color: rgba(0, 255, 255, 0.05);
}

/* 代码块样式 */
.card-cyberpunk pre {
  border: 1px solid rgba(0, 255, 255, 0.3);
  background-color: rgba(13, 14, 25, 0.7);
  padding: 15px;
  overflow-x: auto;
  font-family: "Courier New", monospace;
  font-size: 14px;
  color: #00ffff;
  box-shadow: 0 0 10px rgba(0, 255, 255, 0.1) inset;
}

.card-cyberpunk code:not(pre code) {
  background-color: rgba(0, 255, 255, 0.1);
  color: #00ffff;
  padding: 2px 4px;
  font-family: "Courier New", monospace;
  font-size: 0.9em;
  border: 1px solid rgba(0, 255, 255, 0.2);
}

/* 引用块样式 */
.card-cyberpunk blockquote {
  background-color: rgba(255, 0, 128, 0.1);
  border-left: 3px solid rgba(255, 0, 128, 0.5);
  color: #e0e0e0;
  padding: 15px 20px;
  margin: 20px 0;
  font-style: italic;
  position: relative;
}

.card-cyberpunk blockquote:before {
  content: ">";
  position: absolute;
  top: 5px;
  left: 5px;
  color: rgba(255, 0, 128, 0.3);
  font-size: 24px;
  font-family: monospace;
}

.card-content ol,
.card-content ul {
  padding-left: 0;
}
.card-cyberpunk ul > li:before {
  content: ">";
  position: absolute;
  left: 0;
  top: -3px;
  color: #00ffff;
  font-size: 14px;
  font-weight: bold;
  font-family: monospace;
}

/* 水平线样式 */
.card-cyberpunk hr {
  height: 2px;
  border: none;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(0, 255, 255, 0.5),
    rgba(255, 0, 128, 0.5),
    transparent
  );
  margin: 25px 0;
}

/* 图片样式 */
.card-cyberpunk img {
  max-width: 100%;
  height: auto;
  margin: 20px 0;
  border: 1px solid rgba(0, 255, 255, 0.3);
  box-shadow: 0 0 15px rgba(0, 255, 255, 0.2);
  position: relative;
  filter: contrast(1.1) brightness(1.1);
}

/* 图片故障效果 */
.card-cyberpunk img:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    45deg,
    rgba(255, 0, 128, 0.2),
    rgba(0, 255, 255, 0.2)
  );
  mix-blend-mode: overlay;
  pointer-events: none;
}

/* 添加赛博朋克风格的页脚装饰 */
.card-cyberpunk .card-footer {
  text-align: right;
  margin-top: 30px;
  font-size: 12px;
  color: rgba(0, 255, 255, 0.7);

  letter-spacing: 1px;
  border-top: 1px solid rgba(0, 255, 255, 0.2);
  padding-top: 15px;
  font-family: "Orbitron", "PingFang SC", sans-serif;
}

/* 添加故障文本效果类 */
.card-cyberpunk .glitch-text {
  position: relative;
  color: #00ffff;
}

.card-cyberpunk .glitch-text:before,
.card-cyberpunk .glitch-text:after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

.card-cyberpunk .glitch-text:before {
  left: 2px;
  text-shadow: -1px 0 rgba(255, 0, 128, 0.7);
  clip: rect(44px, 450px, 56px, 0);
  animation: glitch-anim-1 5s infinite linear alternate-reverse;
}

.card-cyberpunk .glitch-text:after {
  left: -2px;
  text-shadow: -1px 0 rgba(0, 255, 255, 0.7);
  clip: rect(44px, 450px, 56px, 0);
  animation: glitch-anim-2 5s infinite linear alternate-reverse;
}

@keyframes glitch-anim-1 {
  0% {
    clip: rect(27px, 9999px, 30px, 0);
  }
  10% {
    clip: rect(42px, 9999px, 78px, 0);
  }
  20% {
    clip: rect(13px, 9999px, 10px, 0);
  }
  30% {
    clip: rect(24px, 9999px, 26px, 0);
  }
  40% {
    clip: rect(76px, 9999px, 81px, 0);
  }
  50% {
    clip: rect(35px, 9999px, 39px, 0);
  }
  60% {
    clip: rect(57px, 9999px, 62px, 0);
  }
  70% {
    clip: rect(10px, 9999px, 16px, 0);
  }
  80% {
    clip: rect(40px, 9999px, 44px, 0);
  }
  90% {
    clip: rect(56px, 9999px, 59px, 0);
  }
  100% {
    clip: rect(89px, 9999px, 98px, 0);
  }
}

/* 添加霓虹文本效果 */
.card-cyberpunk .neon-text {
  color: #fff;
  text-shadow:
    0 0 5px rgba(255, 255, 255, 0.8),
    0 0 10px rgba(255, 255, 255, 0.5),
    0 0 15px rgba(0, 255, 255, 0.5),
    0 0 20px rgba(0, 255, 255, 0.4),
    0 0 30px rgba(0, 255, 255, 0.3);
}

/* 添加终端风格代码块 */
