}

.card-pop-art .card-content {
  position: relative;
  z-index: 2;
  padding: 12px;
  border: 2px solid var(--pop-border);
  height: 100%;
  background: var(--pop-white);
  box-shadow: 10px 10px 0 var(--pop-shadow);
}

/* 标题样式 */
.card-pop-art h1 strong {
  color: var(--pop-primary);
  background-color: var(--pop-white);
  padding: 0 5px;
  border: 3px solid var(--pop-border);
  display: inline-block;
  transform: rotate(-2deg);
}

.card-pop-art h1 {
  font-size: 32px;
  font-weight: 800;
  color: var(--pop-text);
  margin-top: 20px;
  margin-bottom: 20px;
  text-transform: uppercase;
  letter-spacing: 1px;
  text-shadow: 3px 3px 0 var(--pop-primary);
  position: relative;
  padding-bottom: 10px;
}

.card-pop-art h1::after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 6px;
  background: repeating-linear-gradient(
    45deg,
    var(--pop-primary),
    var(--pop-primary) 10px,
    var(--pop-text) 10px,
    var(--pop-text) 20px
  );
}

.card-pop-art h2 {
  font-size: 24px;
  font-weight: 700;
  color: var(--pop-text);
  margin-top: 24px;
  margin-bottom: 16px;
  background-color: var(--pop-accent2);
  padding: 4px 10px;
  transform: rotate(-2deg);
}

.card-pop-art h3 {
  font-size: 20px;
  font-weight: 700;
  color: var(--pop-text);
  margin-top: 20px;
  margin-bottom: 12px;
  background-color: var(--pop-accent3);
  padding: 4px 10px;
  border-radius: 4px;
}

/* 段落样式 */
.card-pop-art p {
  color: var(--pop-text);
}

/* 列表样式 */
.card-pop-art ul {
  background-color: rgba(255, 255, 255, 0.7);
  padding: 15px;
  border: 2px solid var(--pop-border);
  border-radius: 8px;
}

.card-pop-art ul li {
  position: relative;
  padding-left: 25px;
  margin: 10px 0;
}

.card-pop-art ul > li::before {
