}

.card-pop-art ol {
  counter-reset: item;
  background-color: rgba(255, 255, 255, 0.7);
  padding: 15px;
  border: 2px solid var(--pop-border);
  border-radius: 8px;
}

.card-pop-art ol li {
  position: relative;
  padding-left: 35px;
  margin: 10px 0;
  counter-increment: item;
}

.card-pop-art ol > li::before {
  content: attr(data-index);
  position: absolute;
  left: 0;
  top: 0;
  background-color: var(--pop-primary);
  color: var(--pop-white);
  font-weight: 700;
  width: 25px;
  height: 25px;
  border-radius: 50%;
  text-align: center;
  line-height: 25px;
  border: 2px solid var(--pop-border);
}

/* 引用样式 */
.card-pop-art blockquote {
  border-left: 8px solid var(--pop-primary);
  color: var(--pop-text);
  font-style: italic;
  font-weight: 600;
  background-color: rgba(var(--pop-primary-rgb), 0.1);
  border-radius: 0 8px 8px 0;
  position: relative;
}

.card-pop-art blockquote::before {
  content: "";
  position: absolute;
  top: -20px;
  left: 10px;
  font-size: 60px;
  color: var(--pop-primary);
  font-family: Georgia, serif;
}

/* 代码块样式 */
.card-pop-art pre {
  background-color: var(--pop-code-bg);
  border-radius: 8px;
  padding: 15px;
  font-family: "SFMono-Regular", Consolas, "Liberation Mono", Menlo, monospace;
  font-size: 14px;
  overflow-x: auto;
  color: var(--pop-white);
  border: 2px solid var(--pop-primary);
  box-shadow: 5px 5px 0 var(--pop-accent2);
}

.card-pop-art code:not(pre code) {
  background-color: var(--pop-code-bg);
  padding: 3px 6px;
  border-radius: 4px;
  font-family: "SFMono-Regular", Consolas, "Liberation Mono", Menlo, monospace;
  font-size: 0.9em;
  color: var(--pop-white);
  border: 1px solid var(--pop-primary);
}

/* 表格样式 */
.card-pop-art table {
  border-collapse: separate;
  border-spacing: 0;
  width: 100%;
  margin: 20px 0;
  background-color: rgba(255, 255, 255, 0.7);
  border: 3px solid var(--pop-border);
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 5px 5px 0 var(--pop-primary);
}

.card-pop-art th {
  background-color: var(--pop-code-bg);
  color: var(--pop-white);
  padding: 12px;
  text-align: left;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.card-pop-art td {
  border-bottom: 2px solid var(--pop-border);
  padding: 10px;
  position: relative;
}

.card-pop-art tr:nth-child(odd) td {
  background-color: rgba(var(--pop-accent2-rgb), 0.2);
}

/* 水平线样式 */
.card-pop-art hr {
  border: 0;
  height: 6px;
  background: repeating-linear-gradient(
    45deg,
    var(--pop-primary),
    var(--pop-primary) 10px,
    var(--pop-text) 10px,
    var(--pop-text) 20px
  );
  margin: 25px 0;
  border-radius: 3px;
}

/* 图片样式 */
.card-pop-art img {
  max-width: 100%;
  border: 5px solid var(--pop-border);
  border-radius: 0;
  box-shadow: 8px 8px 0 var(--pop-primary);
  transform: rotate(-1deg);
}

/* 高亮标记 */
.card-pop-art mark {
  background-color: var(--pop-primary);
  color: var(--pop-white);
  padding: 2px 5px;
  border-radius: 4px;
  font-weight: 700;
  box-shadow: 2px 2px 0 var(--pop-text);
}

.card-pop-art mark::after {
  display: none;
}

/* 链接样式 */
.card-pop-art a {
  color: var(--pop-primary);
  text-decoration: none;
  font-weight: 700;
  border-bottom: 3px solid var(--pop-border);
  padding: 0 3px;
  transition: all 0.3s ease;
}

.card-pop-art a:hover {
  background-color: var(--pop-primary);
  color: var(--pop-white);
  border-radius: 4px;
}

/* 添加波普艺术特有的语音气泡效果 */
