  border-top-color: var(--pop-white);
}

.card-pop-art .card-footer {
  position: relative;
}

.card-pop-art .card-footer .page {
  font-size: 12px;
  position: absolute;
  right: 8px;
  top: -24px;
  z-index: 100;
}
