.card-cyberpunk:before {
  width: 100%;
  height: 100%;
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background:
    linear-gradient(rgba(18, 16, 36, 0.8) 1px, transparent 1px),
    linear-gradient(90deg, rgba(18, 16, 36, 0.8) 1px, transparent 1px);
  background-size: 20px 20px;
  z-index: 1;
  opacity: 0.2;
}

/* 故障艺术装饰 */
.card-cyberpunk:after {
  content: "";
  position: absolute;
  top: 0;
  left: -10%;
  width: 120%;
  height: 10px;
  background: linear-gradient(
    90deg,
    transparent 5%,
    rgba(0, 255, 255, 0.8) 15%,
    rgba(255, 0, 128, 0.8) 25%,
    transparent 35%
  );
  opacity: 0.7;
  filter: blur(1px);
  transform: skewX(-20deg);
  /* animation: glitch 10s infinite linear; */
  z-index: 2;
}

@keyframes glitch {
  0% {
    top: -10px;
    opacity: 0.7;
  }
  20% {
    top: 40px;
    opacity: 0.4;
  }
  40% {
    top: 80px;
    opacity: 0.6;
  }
  60% {
    top: 120px;
    opacity: 0.3;
  }
  80% {
    top: 180px;
    opacity: 0.5;
  }
  100% {
    top: 240px;
    opacity: 0;
  }
}

