  /* border: 4px outset rgba(139, 154, 122, 0.7); */
}

/* 表格样式 */
.card-meadow-dawn table {
  border-collapse: collapse;
  width: 100%;
  margin-bottom: 1.5em;
}

.card-meadow-dawn th,
.card-meadow-dawn td {
  padding: 0.5em 1em;
  border: 1px solid #d4d9c9;
}

.card-meadow-dawn th {
  background-color: rgba(139, 154, 122, 0.2);
}

/* 无序列表样式 */
.card-meadow-dawn ul {
  list-style-type: none;
  padding-left: 1.5em;
  margin-bottom: 1.5em;
}

.card-meadow-dawn ul li {
  position: relative;
  margin-bottom: 0.5em;
  padding-left: 1.5em;
}

.card-meadow-dawn ul li::before {
  content: "•";
  color: #8b9a7a;
  position: absolute;
  left: 0;
  font-size: 1.2em;
}

/* 有序列表样式 */
.card-meadow-dawn ol {
  list-style: none;
  padding: 0.5em 3.5em;
  counter-reset: decorated-counter;
  margin-bottom: 1.5em;
}

.card-meadow-dawn ol li {
  counter-increment: decorated-counter;
  position: relative;
  margin-bottom: 1.8em;
  padding-left: 1em;
}

.card-meadow-dawn ol li::before {
  content: attr(data-index);
  position: absolute;
  left: -3.5em;
  top: 0;
  width: 2.5em;
  height: 2.5em;
  line-height: 2.5em;
  text-align: center;
  color: #6b8c42;
  background-color: rgba(255, 253, 245, 0.9);
