  border: 1px solid rgba(79, 172, 254, 0.4);
  color: #4facfe;
  text-align: center;
  line-height: 26px;
  font-weight: bold;
  border-radius: 50%;
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
