  margin: 0 0 30px 0;
  letter-spacing: -0.5px;
  font-weight: 700;
  text-align: left;
  line-height: 1.2;
}

/* 副标题样式 */
.card-minimalist h2 {
  font-size: 18px;
  color: #000;
  margin: 30px 0 20px 0;
  font-weight: 600;
  letter-spacing: -0.3px;
  text-align: left;
  position: relative;
  padding-bottom: 8px;
  border-bottom: 1px solid #eee;
}

/* 段落样式 */
.card-minimalist p {
  color: #333;
  font-size: 16px;
}

.card-minimalist strong {
  color: #000;
  font-weight: 600;
}

.card-minimalist em {
  font-style: italic;
  color: #555;
}

.card-minimalist p a {
  color: #000;
  text-decoration: none;
  border-bottom: 1px solid #000;
  transition: all 0.2s ease;
}

.card-minimalist p a:hover {
  opacity: 0.7;
}

/* 有序列表样式 */
.card-minimalist li {
  position: relative;
  counter-increment: item;
  list-style: none;
  margin-bottom: 12px;
  color: #333;
  line-height: 1.6;
  font-size: 16px;
}

/* 序号极简风格 */
.card-minimalist li:before {
  content: attr(data-index);
  float: left;
  margin-right: 8px;
  color: #000;
  font-weight: 600;
}

/* 小标题样式 */
.card-minimalist h3 {
  display: block;
  color: #000;
  margin: 24px 0 8px 0;
  font-size: 16px;
  font-weight: 600;
}

/* 表格样式 */
.card-minimalist table {
  border-collapse: collapse;
  margin: 25px 0;
  width: 100%;
  border-top: 1px solid #000;
  border-bottom: 1px solid #000;
}

.card-minimalist th {
  background-color: #fff;
  color: #000;
  background: rgba(0, 0, 0, 0.05);
  padding: 12px 8px;
  text-align: left;
  font-weight: 600;
  border-bottom: 1px solid #000;
}

.card-minimalist td {
  color: #333;
  padding: 12px 8px;
  border-bottom: 1px solid #eee;
}
.card-minimalist tr:nth-child(even) {
  background-color: #f9f9f9;
}

/* 代码块样式 */
.card-minimalist pre {
  border: 1px solid #eee;
  background-color: #f9f9f9;
  padding: 15px;
  overflow-x: auto;
  font-family: "SF Mono", monospace;
  font-size: 14px;
}

.card-minimalist code:not(pre code) {
  background-color: #f5f5f5;
  color: #000;
  padding: 2px 4px;
  font-family: "SF Mono", monospace;
  font-size: 0.9em;
}

/* 引用块样式 */
.card-minimalist blockquote {
  background-color: #fff;
  border-left: 2px solid #000;
  color: #555;
  padding: 15px 20px;
  margin: 20px 0;
  font-style: italic;
}

.card-minimalist ul > li:before {
  content: "";
  width: 8px;
  height: 8px;
  display: block;
  float: left;
  transform: translate(0px, 8px);
  background-color: #000;
}

/* 水平线样式 */
.card-minimalist hr {
  height: 1px;
  border: none;
  background: #eee;
  margin: 30px 0;
}

/* 图片样式 */
.card-minimalist img {
  max-width: 100%;
  height: auto;
  margin: 25px 0;
  border: none;
}

/* 添加极简风格的页脚装饰 */
.card-minimalist .card-footer {
  color: #999;
  border-top: 1px solid #eee;
  text-align: right;
}

/* 强调色使用 */
.card-minimalist .highlight {
  background-color: #000;
  color: #fff;
  padding: 0 3px;
}
