  border: 1px solid rgba(0, 255, 255, 0.3);
  background-color: rgba(13, 14, 25, 0.7);
  padding: 15px;
  overflow-x: auto;
  font-family: "Courier New", monospace;
  font-size: 14px;
  color: #00ffff;
  box-shadow: 0 0 10px rgba(0, 255, 255, 0.1) inset;
}

.card-cyberpunk code:not(pre code) {
  background-color: rgba(0, 255, 255, 0.1);
  color: #00ffff;
  padding: 2px 4px;
  font-family: "Courier New", monospace;
  font-size: 0.9em;
  border: 1px solid rgba(0, 255, 255, 0.2);
}

/* 引用块样式 */
.card-cyberpunk blockquote {
  background-color: rgba(255, 0, 128, 0.1);
  border-left: 3px solid rgba(255, 0, 128, 0.5);
  color: #e0e0e0;
  padding: 15px 20px;
  margin: 20px 0;
  font-style: italic;
  position: relative;
}

.card-cyberpunk blockquote:before {
  content: ">";
  position: absolute;
  top: 5px;
  left: 5px;
  color: rgba(255, 0, 128, 0.3);
  font-size: 24px;
  font-family: monospace;
}

.card-content ol,
.card-content ul {
  padding-left: 0;
}
.card-cyberpunk ul > li:before {
  content: ">";
  position: absolute;
  left: 0;
  top: -3px;
  color: #00ffff;
  font-size: 14px;
  font-weight: bold;
  font-family: monospace;
}

/* 水平线样式 */
.card-cyberpunk hr {
  height: 2px;
  border: none;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(0, 255, 255, 0.5),
    rgba(255, 0, 128, 0.5),
    transparent
  );
  margin: 25px 0;
}

/* 图片样式 */
.card-cyberpunk img {
  max-width: 100%;
  height: auto;
  margin: 20px 0;
  border: 1px solid rgba(0, 255, 255, 0.3);
  box-shadow: 0 0 15px rgba(0, 255, 255, 0.2);
  position: relative;
  filter: contrast(1.1) brightness(1.1);
}

/* 图片故障效果 */
.card-cyberpunk img:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    45deg,
    rgba(255, 0, 128, 0.2),
    rgba(0, 255, 255, 0.2)
  );
  mix-blend-mode: overlay;
  pointer-events: none;
}

/* 添加赛博朋克风格的页脚装饰 */
.card-cyberpunk .card-footer {
  text-align: right;
  margin-top: 30px;
  font-size: 12px;
  color: rgba(0, 255, 255, 0.7);

  letter-spacing: 1px;
  border-top: 1px solid rgba(0, 255, 255, 0.2);
  padding-top: 15px;
  font-family: "Orbitron", "PingFang SC", sans-serif;
}

/* 添加故障文本效果类 */
.card-cyberpunk .glitch-text {
  position: relative;
  color: #00ffff;
}

.card-cyberpunk .glitch-text:before,
.card-cyberpunk .glitch-text:after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

.card-cyberpunk .glitch-text:before {
  left: 2px;
  text-shadow: -1px 0 rgba(255, 0, 128, 0.7);
  clip: rect(44px, 450px, 56px, 0);
  animation: glitch-anim-1 5s infinite linear alternate-reverse;
}

.card-cyberpunk .glitch-text:after {
  left: -2px;
  text-shadow: -1px 0 rgba(0, 255, 255, 0.7);
  clip: rect(44px, 450px, 56px, 0);
  animation: glitch-anim-2 5s infinite linear alternate-reverse;
}

@keyframes glitch-anim-1 {
  0% {
    clip: rect(27px, 9999px, 30px, 0);
  }
  10% {
    clip: rect(42px, 9999px, 78px, 0);
  }
  20% {
    clip: rect(13px, 9999px, 10px, 0);
  }
  30% {
    clip: rect(24px, 9999px, 26px, 0);
  }
  40% {
    clip: rect(76px, 9999px, 81px, 0);
  }
  50% {
    clip: rect(35px, 9999px, 39px, 0);
  }
  60% {
    clip: rect(57px, 9999px, 62px, 0);
  }
  70% {
    clip: rect(10px, 9999px, 16px, 0);
  }
  80% {
    clip: rect(40px, 9999px, 44px, 0);
  }
  90% {
    clip: rect(56px, 9999px, 59px, 0);
  }
  100% {
    clip: rect(89px, 9999px, 98px, 0);
  }
}

/* 添加霓虹文本效果 */
.card-cyberpunk .neon-text {
  color: #fff;
  text-shadow:
    0 0 5px rgba(255, 255, 255, 0.8),
    0 0 10px rgba(255, 255, 255, 0.5),
    0 0 15px rgba(0, 255, 255, 0.5),
    0 0 20px rgba(0, 255, 255, 0.4),
    0 0 30px rgba(0, 255, 255, 0.3);
}

/* 添加终端风格代码块 */
.card-cyberpunk .terminal {
  background: rgba(0, 0, 0, 0.7);
  border: 1px solid rgba(0, 255, 255, 0.3);
  padding: 15px;
  font-family: "Courier New", monospace;
  color: #00ffff;
  position: relative;
  margin: 20px 0;
}

.card-cyberpunk .terminal:before {
  content: "> ";
  color: #00ffff;
  position: absolute;
  top: 15px;
  left: 15px;
}

.card-cyberpunk .terminal code {
  display: block;
  padding-left: 15px;
  line-height: 1.5;
}

/* 添加数据面板效果 */
.card-cyberpunk .data-panel {
  background: rgba(13, 14, 25, 0.8);
  border: 1px solid rgba(0, 255, 255, 0.3);
  padding: 15px;
  margin: 20px 0;
  position: relative;
}

.card-cyberpunk .data-panel:before {
  content: "DATA";
  position: absolute;
  top: -10px;
  left: 10px;
  background: #0d0e19;
  color: #00ffff;
  font-size: 12px;
  padding: 0 5px;
  font-family: "Orbitron", sans-serif;
}

/* 添加扫描线效果 */
.card-cyberpunk .card-content:after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    to bottom,
    transparent 50%,
    rgba(0, 0, 0, 0.05) 50%
  );
  background-size: 100% 4px;
  pointer-events: none;
  z-index: 4;
  opacity: 0.3;
}

/* 添加霓虹边框效果 */
.card-cyberpunk .neon-border {
  border: 1px solid rgba(0, 255, 255, 0.5);
  box-shadow:
    0 0 5px rgba(0, 255, 255, 0.5),
    0 0 10px rgba(0, 255, 255, 0.3),
    inset 0 0 5px rgba(0, 255, 255, 0.2);
  padding: 15px;
  margin: 20px 0;
}

/* 添加故障分隔线 */
.card-cyberpunk .glitch-divider {
  height: 3px;
  width: 100%;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(255, 0, 128, 0.5),
    rgba(0, 255, 255, 0.5),
    transparent
  );
  position: relative;
  margin: 25px 0;
}

.card-cyberpunk .glitch-divider:before {
  content: "";
  position: absolute;
  top: -2px;
  left: 30%;
  width: 40%;
  height: 7px;
  background: rgba(255, 0, 128, 0.5);
  filter: blur(4px);
  animation: divider-glitch 3s infinite alternate;
}

@keyframes divider-glitch {
  0%,
  100% {
    opacity: 0.5;
    transform: translateX(0);
  }
  50% {
    opacity: 0.8;
    transform: translateX(30px);
  }
  75% {
    opacity: 0.6;
    transform: translateX(-20px);
  }
}

/* 添加数字化噪点效果 */
.card-cyberpunk .noise {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: url("data:image/png;base64,");
  opacity: 0.05;
  z-index: 5;
  pointer-events: none;
}

/* 添加霓虹按钮效果 */
.card-cyberpunk .neon-button {
  display: inline-block;
  background: rgba(0, 0, 0, 0.7);
  color: #00ffff;
  border: 1px solid #00ffff;
  padding: 8px 16px;
  margin: 10px 0;
  font-family: "Orbitron", sans-serif;

  letter-spacing: 1px;
  font-size: 14px;
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;
  box-shadow: 0 0 5px rgba(0, 255, 255, 0.5);
}

.card-cyberpunk .neon-button:hover {
  background: rgba(0, 255, 255, 0.1);
  color: #fff;
  box-shadow:
    0 0 5px rgba(0, 255, 255, 0.8),
    0 0 10px rgba(0, 255, 255, 0.5),
    0 0 15px rgba(0, 255, 255, 0.3);
}

/* 添加数据标签效果 */
.card-cyberpunk .data-tag {
  display: inline-block;
  background: rgba(255, 0, 128, 0.1);
  color: #ff0080;
  border: 1px solid rgba(255, 0, 128, 0.5);
  padding: 2px 8px;
  margin: 0 5px;
  font-family: "Rajdhani", sans-serif;
  font-size: 12px;

  letter-spacing: 1px;
}

/* 添加高亮区块 */
.card-cyberpunk .highlight-block {
  background: rgba(0, 0, 0, 0.3);
  border-left: 3px solid #00ffff;
  padding: 15px;
  margin: 20px 0;
  position: relative;
}

.card-cyberpunk .highlight-block:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, rgba(0, 255, 255, 0.1), transparent 70%);
  pointer-events: none;
}

/* 添加警告区块 */
.card-cyberpunk .warning-block {
  background: rgba(0, 0, 0, 0.3);
  border-left: 3px solid #ff0080;
  padding: 15px;
  margin: 20px 0;
  position: relative;
}

.card-cyberpunk .warning-block:before {
  content: "!";
  position: absolute;
  top: 15px;
  left: -15px;
  width: 25px;
  height: 25px;
  background: #ff0080;
  color: #000;
  border-radius: 50%;
  text-align: center;
  line-height: 25px;
  font-weight: bold;
  font-family: "Orbitron", sans-serif;
}

/* 添加卡片头部装饰 */
.card-cyberpunk .card-header {
  position: relative;
  height: 5px;
  background: linear-gradient(90deg, #ff0080, #00ffff);
  margin-bottom: 20px;
  flex: 0 0 auto;
}

.card-cyberpunk .card-header:before {
  content: "CYBER";
  position: absolute;
  top: 10px;
  right: 0;
  font-size: 10px;
  color: #00ffff;
  font-family: "Orbitron", sans-serif;
  letter-spacing: 1px;
  text-shadow: 0 0 5px rgba(0, 255, 255, 0.7);
}

/* 添加卡片底部装饰 */
.card-cyberpunk .card-footer {
  position: relative;
  margin-top: 30px;
  padding-top: 15px;
  border-top: 1px solid rgba(0, 255, 255, 0.3);
  font-size: 10px;
  color: rgba(0, 255, 255, 0.7);
  font-family: "Orbitron", sans-serif;

  letter-spacing: 1px;
  text-align: right;
}

.card-cyberpunk .card-footer:after {
  content: "// END TRANSMISSION";
  position: absolute;
  bottom: 0px;
  left: 0;
  font-size: 8px;
  color: rgba(255, 0, 128, 0.7);
}

/* 添加进度条效果 */
.card-cyberpunk .progress-bar {
  width: 100%;
  height: 10px;
  background: rgba(0, 0, 0, 0.3);
  margin: 15px 0;
  position: relative;
  overflow: hidden;
}

.card-cyberpunk .progress-bar:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 60%;
  background: linear-gradient(
    90deg,
    rgba(0, 255, 255, 0.7),
    rgba(0, 255, 255, 0.3)
  );
  /* animation: progress 2s infinite linear; */
}

@keyframes progress {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

/* 添加全局故障动画效果 */
.card-cyberpunk {
  animation: card-glitch 10s infinite;
}

@keyframes card-glitch {
  0%,
  100% {
    filter: none;
  }
  92% {
    filter: none;
  }
  92.5% {
    filter: hue-rotate(90deg) brightness(1.1);
  }
  93% {
    filter: none;
  }
  93.5% {
    filter: brightness(1.2) contrast(1.2);
  }
  94% {
    filter: none;
  }
  98% {
    filter: none;
  }
  98.2% {
    filter: brightness(1.2) contrast(1.3) hue-rotate(-20deg);
  }
  98.4% {
    filter: none;
  }
}

/* 添加数据标记效果 */
.card-cyberpunk mark {
  position: relative;
  background: linear-gradient(
    transparent 20%,
    rgba(0, 255, 255, 0.2) 20%,
    rgba(0, 255, 255, 0.2) 80%,
    transparent 80%
  );
  padding: 0 3px;
  color: #00ffff;
}

.card-cyberpunk mark::after {
  display: none;
}
.card-cyberpunk mark:before {
  content: "ALERT";
  position: absolute;
  top: -15px;
  left: 0;
  font-size: 8px;
  color: rgba(0, 255, 255, 0.7);
  font-family: "Rajdhani", sans-serif;
}

.card-cyberpunk .card-footer {
  text-align: right;
}

.card-cyberpunk .card-footer .page {
  font-size: 12px;
}
