  font-size: 0.9em;
  font-weight: bold;
  box-shadow: 0 2px 8px rgba(139, 154, 122, 0.2);
}

.card-meadow-dawn ol li::after {
  content: "";
  position: absolute;
  left: -0.5em;
  bottom: -0.5em;
