  background: linear-gradient(135deg, #f5f7ff 0%, #e8f0ff 100%);
  box-shadow: 0 15px 35px rgba(149, 175, 255, 0.2);
  position: relative;
}

.card-cover-dreamy:before {
  content: "";
  position: absolute;
  top: -50px;
  right: -50px;
  width: 150px;
  height: 150px;
  background: linear-gradient(
    135deg,
    rgba(190, 205, 255, 0.5),
    rgba(190, 205, 255, 0)
  );
  border-radius: 50%;
  z-index: 10;
}

.card-cover-dreamy:after {
  content: "";
  position: absolute;
  bottom: -30px;
  left: -30px;
  width: 100px;
  height: 100px;
  background: linear-gradient(
    135deg,
    rgba(190, 205, 255, 0.5),
    rgba(190, 205, 255, 0)
  );
  border-radius: 50%;
  z-index: 10;
}

.card-cover-dreamy .cover-overlay {
  background: linear-gradient(
    to bottom,
    rgba(124, 141, 255, 0.1),
    rgba(124, 141, 255, 0.6)
  );
}

/* 覆盖式布局 */
.card-cover-overlay.card-cover-dreamy .cover-content {
  background: linear-gradient(
    to top,
    rgba(245, 247, 255, 0.95),
    rgba(245, 247, 255, 0.8) 50%,
    rgba(245, 247, 255, 0) 100%
  );
  padding-top: 70px;
  z-index: 20;
}

/* 分离式布局 */
.card-cover-separate.card-cover-dreamy .cover-content {
  background: linear-gradient(135deg, #f5f7ff 0%, #e8f0ff 100%);
  padding: 24px;
  z-index: 20;
}

.card-cover-dreamy .cover-title {
  font-size: 28px;
  color: #5e6fff;
  margin-bottom: 12px;
  letter-spacing: 0.5px;
  font-weight: 600;
  text-shadow: 0 2px 10px rgba(124, 141, 255, 0.2);
}

.card-cover-dreamy .cover-description {
  color: #666;
}

.card-cover-dreamy .cover-tag {
  background: linear-gradient(135deg, #a5b4ff, #7c8dff);
  color: white;
  padding: 5px 14px;
  border-radius: 20px;
  box-shadow: 0 4px 10px rgba(124, 141, 255, 0.3);
  transition: all 0.3s ease;
}

.card-cover-dreamy .cover-tag:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 12px rgba(124, 141, 255, 0.4);
}

.card-cover-dreamy .cover-date {
  color: #8c9eff;
}

.card-dreamy {
  width: 100%;
  background: linear-gradient(135deg, #f5f7ff 0%, #e8f0ff 100%);
  box-shadow: 0 15px 35px rgba(149, 175, 255, 0.2);
  padding: 30px;
