  );
  border-radius: 50%;
}

.card-dreamy .card-content {
  position: relative;
  z-index: 1;
  height: calc(var(--card-height) - 100px);
}
/* 段落样式 */
.card-dreamy p {
  color: #666;
  font-size: 15px;
}

.card-dreamy strong {
  color: #5e6fff;
  font-weight: 600;
}

.card-dreamy em {
  color: #8c9eff;
  font-style: italic;
}

.card-dreamy p a {
  color: #7c8dff;
  text-decoration: none;
  border-bottom: 1px dashed #d8e1ff;
  transition: all 0.3s ease;
}

.card-dreamy p a:hover {
  color: #5e6fff;
  border-bottom: 1px solid #a5b4ff;
}

.card-dreamy h1 {
  font-size: 28px;
  color: #5e6fff;
  margin-bottom: 8px;
  letter-spacing: 0.5px;
  font-weight: 600;
}

.card-dreamy h2 {
  font-size: 18px;
  color: #8c9eff;
  margin-bottom: 28px;
  font-weight: 400;
  letter-spacing: 0.5px;
}

.card-dreamy li {
  position: relative;
  counter-increment: item;
  list-style: none;
  margin: 16px 0;
  padding-left: 46px;
  color: #666;
  font-size: 15px;
}

.card-content ol,
.card-content ul {
  padding-left: 0;
}
.card-dreamy ul > li {
  padding-left: 24px;
}

.card-dreamy ol > li:before {
  content: attr(data-index);
  position: absolute;
  left: 0;
  top: -2px;
  background: linear-gradient(135deg, #a5b4ff, #7c8dff);
  color: white;
  width: 32px;
  height: 32px;
  border-radius: 50%;
  text-align: center;
  line-height: 32px;
  font-weight: bold;
  box-shadow: 0 4px 10px rgba(124, 141, 255, 0.3);
}

.card-dreamy h3 {
  display: block;
  color: #5e6fff;
  margin-bottom: 6px;
  font-size: 17px;
}
/* 表格样式 */
.card-dreamy table {
  box-shadow: 0 4px 15px rgba(149, 175, 255, 0.15);
}

.card-dreamy th {
  background: linear-gradient(135deg, #f5f7ff 0%, #e8f0ff 100%);
  color: #7c8dff;
  border-bottom: 2px solid #d8e1ff;
}

.card-dreamy td {
  color: #666;
}

.card-dreamy tr {
  border-bottom-color: #e8f0ff;
}

.card-dreamy tr:nth-child(even) {
  background-color: #f9faff;
}

/* 代码块样式 */
.card-dreamy pre {
  border: 1px solid #e5e7eb;
  box-shadow: 0 4px 15px rgba(149, 175, 255, 0.15);
}

.card-dreamy code:not(pre code) {
  background-color: #e8f0ff;
  color: #5e6fff;
}

/* 引用块样式 */
.card-dreamy blockquote {
  background: linear-gradient(135deg, #f5f7ff 0%, #e8f0ff 100%);
  border-left: 4px solid #7c8dff;
  color: #7c8dff;
}

/* 无序列表样式 */
.card-dreamy ul > li:before {
  content: "";
  position: absolute;
  left: 0;
  top: 8px;
  width: 10px;
  height: 10px;
  background: linear-gradient(135deg, #a5b4ff, #7c8dff);
  border-radius: 50%;
  box-shadow: 0 2px 5px rgba(124, 141, 255, 0.3);
}

/* 水平线样式 */
.card-dreamy hr {
  background: linear-gradient(to right, transparent, #7c8dff, transparent);
}

.card-dreamy .card-footer {
  text-align: right;
}

.card-dreamy .card-footer .page {
  font-size: 12px;
  color: #6a737d;
}
