/* 添加扫描线效果 */
.card-cyberpunk .card-content:after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    to bottom,
    transparent 50%,
    rgba(0, 0, 0, 0.05) 50%
  );
  background-size: 100% 4px;
  pointer-events: none;
  z-index: 4;
  opacity: 0.3;
}

/* 添加霓虹边框效果 */
.card-cyberpunk .neon-border {
  border: 1px solid rgba(0, 255, 255, 0.5);
  box-shadow:
    0 0 5px rgba(0, 255, 255, 0.5),
    0 0 10px rgba(0, 255, 255, 0.3),
    inset 0 0 5px rgba(0, 255, 255, 0.2);
  padding: 15px;
  margin: 20px 0;
}

/* 添加故障分隔线 */
.card-cyberpunk .glitch-divider {
  height: 3px;
  width: 100%;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(255, 0, 128, 0.5),
    rgba(0, 255, 255, 0.5),
    transparent
  );
  position: relative;
  margin: 25px 0;
}

.card-cyberpunk .glitch-divider:before {
  content: "";
  position: absolute;
  top: -2px;
  left: 30%;
  width: 40%;
  height: 7px;
  background: rgba(255, 0, 128, 0.5);
  filter: blur(4px);
  animation: divider-glitch 3s infinite alternate;
}

@keyframes divider-glitch {
  0%,
  100% {
    opacity: 0.5;
    transform: translateX(0);
  }
  50% {
    opacity: 0.8;
    transform: translateX(30px);
  }
  75% {
    opacity: 0.6;
    transform: translateX(-20px);
  }
}

/* 添加数字化噪点效果 */
.card-cyberpunk .noise {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: url("data:image/png;base64,");
  opacity: 0.05;
  z-index: 5;
  pointer-events: none;
}

/* 添加霓虹按钮效果 */
.card-cyberpunk .neon-button {
  display: inline-block;
  background: rgba(0, 0, 0, 0.7);
  color: #00ffff;
  border: 1px solid #00ffff;
  padding: 8px 16px;
  margin: 10px 0;
  font-family: "Orbitron", sans-serif;

  letter-spacing: 1px;
  font-size: 14px;
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;
  box-shadow: 0 0 5px rgba(0, 255, 255, 0.5);
}

.card-cyberpunk .neon-button:hover {
  background: rgba(0, 255, 255, 0.1);
  color: #fff;
  box-shadow:
    0 0 5px rgba(0, 255, 255, 0.8),
    0 0 10px rgba(0, 255, 255, 0.5),
    0 0 15px rgba(0, 255, 255, 0.3);
}

/* 添加数据标签效果 */
.card-cyberpunk .data-tag {
  display: inline-block;
  background: rgba(255, 0, 128, 0.1);
  color: #ff0080;
  border: 1px solid rgba(255, 0, 128, 0.5);
  padding: 2px 8px;
  margin: 0 5px;
  font-family: "Rajdhani", sans-serif;
  font-size: 12px;

  letter-spacing: 1px;
}

/* 添加高亮区块 */
.card-cyberpunk .highlight-block {
  background: rgba(0, 0, 0, 0.3);
  border-left: 3px solid #00ffff;
  padding: 15px;
  margin: 20px 0;
  position: relative;
}

.card-cyberpunk .highlight-block:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, rgba(0, 255, 255, 0.1), transparent 70%);
  pointer-events: none;
}

/* 添加警告区块 */
.card-cyberpunk .warning-block {
  background: rgba(0, 0, 0, 0.3);
  border-left: 3px solid #ff0080;
  padding: 15px;
  margin: 20px 0;
  position: relative;
}

.card-cyberpunk .warning-block:before {
  content: "!";
  position: absolute;
  top: 15px;
  left: -15px;
  width: 25px;
  height: 25px;
  background: #ff0080;
  color: #000;
  border-radius: 50%;
  text-align: center;
  line-height: 25px;
  font-weight: bold;
  font-family: "Orbitron", sans-serif;
}

/* 添加卡片头部装饰 */
.card-cyberpunk .card-header {
  position: relative;
  height: 5px;
  background: linear-gradient(90deg, #ff0080, #00ffff);
  margin-bottom: 20px;
  flex: 0 0 auto;
}

.card-cyberpunk .card-header:before {
  content: "CYBER";
  position: absolute;
  top: 10px;
  right: 0;
  font-size: 10px;
  color: #00ffff;
  font-family: "Orbitron", sans-serif;
  letter-spacing: 1px;
  text-shadow: 0 0 5px rgba(0, 255, 255, 0.7);
}

/* 添加卡片底部装饰 */
.card-cyberpunk .card-footer {
  position: relative;
  margin-top: 30px;
  padding-top: 15px;
  border-top: 1px solid rgba(0, 255, 255, 0.3);
  font-size: 10px;
  color: rgba(0, 255, 255, 0.7);
  font-family: "Orbitron", sans-serif;

  letter-spacing: 1px;
  text-align: right;
}

.card-cyberpunk .card-footer:after {
  content: "// END TRANSMISSION";
  position: absolute;
  bottom: 0px;
  left: 0;
  font-size: 8px;
  color: rgba(255, 0, 128, 0.7);
}

/* 添加进度条效果 */
.card-cyberpunk .progress-bar {
  width: 100%;
  height: 10px;
  background: rgba(0, 0, 0, 0.3);
  margin: 15px 0;
  position: relative;
  overflow: hidden;
}

.card-cyberpunk .progress-bar:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 60%;
  background: linear-gradient(
    90deg,
    rgba(0, 255, 255, 0.7),
    rgba(0, 255, 255, 0.3)
  );
  /* animation: progress 2s infinite linear; */
}

@keyframes progress {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

/* 添加全局故障动画效果 */
.card-cyberpunk {
  animation: card-glitch 10s infinite;
}

@keyframes card-glitch {
  0%,
  100% {
    filter: none;
  }
  92% {
    filter: none;
  }
  92.5% {
    filter: hue-rotate(90deg) brightness(1.1);
  }
  93% {
    filter: none;
  }
  93.5% {
    filter: brightness(1.2) contrast(1.2);
  }
  94% {
    filter: none;
  }
  98% {
    filter: none;
  }
  98.2% {
    filter: brightness(1.2) contrast(1.3) hue-rotate(-20deg);
  }
  98.4% {
    filter: none;
  }
}

/* 添加数据标记效果 */
.card-cyberpunk mark {
  position: relative;
  background: linear-gradient(
    transparent 20%,
    rgba(0, 255, 255, 0.2) 20%,
    rgba(0, 255, 255, 0.2) 80%,
    transparent 80%
  );
  padding: 0 3px;
  color: #00ffff;
}

.card-cyberpunk mark::after {
  display: none;
}
.card-cyberpunk mark:before {
  content: "ALERT";
  position: absolute;
  top: -15px;
  left: 0;
  font-size: 8px;
  color: rgba(0, 255, 255, 0.7);
  font-family: "Rajdhani", sans-serif;
}

.card-cyberpunk .card-footer {
  text-align: right;
}

.card-cyberpunk .card-footer .page {
  font-size: 12px;
}
