  font-size: 1.6rem;
}

.card-meadow-dawn h3 {
  font-size: 1.4rem;
}

/* 链接样式 */
.card-meadow-dawn a {
  color: #8b9a7a;
  text-decoration: none;
  border-bottom: 1px dotted #8b9a7a;
}

.card-meadow-dawn a:hover {
  color: #6b8c42;
  border-bottom-style: solid;
}

/* 引用样式 */
.card-meadow-dawn blockquote {
  border-left: 4px solid #8b9a7a;
  padding-left: 1em;
