/* ===== 玻璃拟态风格 ===== */
.card-glassmorphism {
  width: 100%;
  background: #161616;
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  box-shadow:
    0 8px 32px rgba(31, 38, 135, 0.2),
    0 0 0 1px rgba(255, 255, 255, 0.1) inset;
  padding: 30px 25px;
  font-family: "SF Pro Display", "PingFang SC", sans-serif;
  position: relative;
  color: rgba(255, 255, 255, 0.85);
}
.card-glassmorphism:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(#f00, #f0f);
  clip-path: circle(50px at 88% 95%);
}

.card-glassmorphism:after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(#2196f3, #e91e63);
  clip-path: circle(50px at 50px 50px);
}
.card-glassmorphism .card-content {
  position: relative;
  z-index: 1;
  border-radius: 12px;
  padding: 24px;
  height: 100%;
  box-shadow: 20px 20px 50px rgba(0, 0, 0, 0.5);
  border-radius: 15px;
  border-top: 1px solid rgba(255, 255, 255, 0.5);
  border-left: 1px solid rgba(255, 255, 255, 0.5);
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(5px);
}

.card-glassmorphism .card-content-inner {
  position: relative;
  z-index: 1;
}

/* 主标题样式 */
.card-glassmorphism h1 {
  font-size: 28px;
  color: #00f2fe;
  margin-bottom: 15px;
  letter-spacing: 0.5px;
  font-weight: 600;
  position: relative;
  text-align: center;
}

.card-glassmorphism h1:after {
  content: "";
  position: absolute;
  bottom: -8px;
  left: 50%;
  transform: translateX(-50%);
  width: 60px;
  height: 2px;
  background: linear-gradient(90deg, #4facfe, #00f2fe);
}

/* 副标题样式 */
.card-glassmorphism h2 {
  font-size: 16px;
  color: rgba(255, 255, 255, 0.8);
  margin-bottom: 24px;
  font-weight: 400;
  letter-spacing: 0.5px;
  text-align: center;
  position: relative;
}

/* 小标题样式 */
.card-glassmorphism h3 {
  display: block;
  color: #00f2fe;
  margin-bottom: 6px;
  font-size: 16px;
  font-weight: 600;
  letter-spacing: 0.5px;
}

/* 段落样式 */
.card-glassmorphism p {
  color: rgba(255, 255, 255, 0.85);
  font-size: 15px;
  position: relative;
}

.card-glassmorphism strong {
  color: #00f2fe;
  font-weight: 600;
}

.card-glassmorphism em {
  color: #00f2fe;
  font-style: italic;
}

.card-glassmorphism a {
  color: #4facfe;
  text-decoration: none;
  border-bottom: 1px dashed rgba(79, 172, 254, 0.5);
  transition: all 0.3s ease;
  position: relative;
}

.card-glassmorphism a:hover {
  color: #00f2fe;
  border-bottom: 1px solid #00f2fe;
  text-shadow: 0 0 5px rgba(0, 242, 254, 0.3);
}

/* 有序列表样式 */
.card-glassmorphism ol {
  counter-reset: item;
  padding-left: 0;
  margin: 20px 0;
}

.card-glassmorphism ol > li {
  position: relative;
  counter-increment: item;
  list-style: none;
  margin-bottom: 16px;
  padding-left: 40px;
  color: rgba(255, 255, 255, 0.85);
  line-height: 1.6;
  font-size: 15px;
}

/* 序号样式 */
.card-glassmorphism ol > li:before {
  content: attr(data-index);
  position: absolute;
  left: 0;
  top: -3px;
  width: 28px;
  height: 28px;
  background: rgba(79, 172, 254, 0.2);
  border: 1px solid rgba(79, 172, 254, 0.4);
  color: #4facfe;
  text-align: center;
  line-height: 26px;
  font-weight: bold;
  border-radius: 50%;
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
}

/* 表格样式 */
.card-glassmorphism table {
  width: 100%;
  border-collapse: collapse;
  margin: 20px 0;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  overflow: hidden;
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.card-glassmorphism th {
  background: rgba(79, 172, 254, 0.2);
  color: rgba(255, 255, 255, 0.9);
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
  padding: 12px;
  font-weight: 600;
  text-align: left;
}

.card-glassmorphism td {
  color: rgba(255, 255, 255, 0.8);
  padding: 12px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.card-glassmorphism tr:last-child td {
  border-bottom: none;
}

.card-glassmorphism tr:nth-child(even) {
  background: rgba(255, 255, 255, 0.05);
}

/* 代码块样式 */
.card-glassmorphism pre {
  background: rgba(0, 0, 0, 0.2) !important;
  border: 1px solid rgba(255, 255, 255, 0.18);
  border-radius: 8px;
  padding: 16px;
  margin: 20px 0;
  overflow-x: auto;
  position: relative;
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
}

.card-glassmorphism pre:before {
  content: "</>";
  position: absolute;
  top: 4px;
  right: 10px;
  background: rgba(0, 0, 0, 0.6);
  color: #4facfe;
  font-size: 10px;
  padding: 2px 8px;
  border-radius: 4px;
  border: 1px solid rgba(79, 172, 254, 0.3);
}

.card-glassmorphism code:not(pre code) {
  background: rgba(79, 172, 254, 0.15);
  color: #00f2fe;
  padding: 2px 6px;
  border-radius: 4px;
  font-family: "SF Mono", "Menlo", monospace;
  font-size: 0.9em;
  border: 1px solid rgba(79, 172, 254, 0.2);
}

/* 引用块样式 */
.card-glassmorphism blockquote {
  background: rgba(0, 0, 0, 0.1);
  border-left: 3px solid #4facfe;
  color: rgba(255, 255, 255, 0.8);
  padding: 15px 20px;
  margin: 20px 0;
  font-style: italic;
  position: relative;
  border-radius: 0 8px 8px 0;
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
}

.card-glassmorphism blockquote:before {
  content: "“";
  position: absolute;
  left: 10px;
  top: 5px;
  color: rgba(79, 172, 254, 0.3);
  font-size: 30px;
  font-family: Georgia, serif;
}

/* 无序列表样式 */
.card-glassmorphism ul {
  padding-left: 0;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 15px;
  margin: 20px 0;
}

.card-glassmorphism ul > li::before {
  display: none;
}
.card-glassmorphism ul > li {
  margin: 0;
  flex: 1;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
  border-radius: 8px;
  padding: 15px;
  border: 1px solid rgba(255, 255, 255, 0.18);
  transition: all 0.3s ease;
}

.card-glassmorphism ul > li:hover {
  transform: translateY(-5px);
  box-shadow: 0 8px 20px rgba(31, 38, 135, 0.2);
}

/* 水平线样式 */
.card-glassmorphism hr {
  border: 0;
  height: 1px;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(79, 172, 254, 0.5),
    transparent
  );
  margin: 25px 0;
}

/* 图片样式 */
.card-glassmorphism img {
  max-width: 100%;
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 8px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
  margin: 20px 0;
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
}

/* 特殊元素：信息卡片 */
.card-glassmorphism .info-card {
  background: rgba(79, 172, 254, 0.1);
  border: 1px solid rgba(79, 172, 254, 0.3);
  border-radius: 8px;
  padding: 15px;
  margin: 20px 0;
  position: relative;
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
}

.card-glassmorphism .info-card:before {
  content: "i";
  position: absolute;
  top: -10px;
  left: 15px;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background: #4facfe;
  color: white;
  text-align: center;
  line-height: 20px;
  font-weight: bold;
  font-style: italic;
  font-size: 12px;
}

/* 特殊元素：标签 */
.card-glassmorphism .tag {
  display: inline-block;
  background: rgba(79, 172, 254, 0.15);
  color: #4facfe;
  padding: 3px 10px;
  border-radius: 20px;
  font-size: 12px;
  margin-right: 8px;
  margin-bottom: 8px;
  border: 1px solid rgba(79, 172, 254, 0.3);
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
  transition: all 0.3s ease;
}

.card-glassmorphism .tag:hover {
  background: rgba(79, 172, 254, 0.25);
  transform: translateY(-2px);
  box-shadow: 0 4px 8px rgba(79, 172, 254, 0.2);
}

/* 封面样式*/
.card-cover-glassmorphism {
  background: #161616;
}

/* 覆盖式布局 */
.card-cover-overlay.card-cover-glassmorphism .cover-content {
  background: linear-gradient(
    to top,
    rgba(255, 255, 255, 0.15) 0%,
    rgba(255, 255, 255, 0.1) 50%,
    rgba(255, 255, 255, 0.05) 100%
  );
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  padding-top: 70px;
  border-top: 1px solid rgba(255, 255, 255, 0.2);
}

/* 分离式布局 */
.card-cover-separate.card-cover-glassmorphism .cover-content {
  background: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  padding: 24px;
  border-top: none;
  margin-top: -30px;
  position: relative;
  border: 1px solid rgba(255, 255, 255, 0.18);
  box-shadow: 0 8px 32px rgba(31, 38, 135, 0.2);
}

.card-cover-separate.card-cover-glassmorphism .cover-content:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 1px;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(255, 255, 255, 0.5),
    transparent
  );
}

.card-cover-glassmorphism .cover-title {
  font-family: "SF Pro Display", "PingFang SC", sans-serif;
  color: #00f2fe;
  position: relative;
  letter-spacing: 0.5px;
  text-align: center;
  margin-bottom: 20px;
  font-weight: 600;
}

.card-cover-glassmorphism .cover-title:after {
  content: "";
  position: absolute;
  bottom: -10px;
  left: 50%;
  transform: translateX(-50%);
  width: 60px;
  height: 2px;
  background: linear-gradient(90deg, #4facfe, #00f2fe);
}

.card-cover-glassmorphism .cover-description {
  color: rgba(255, 255, 255, 0.85);
  text-align: center;
}

.card-cover-glassmorphism .cover-tag {
  background: rgba(79, 172, 254, 0.15);
  color: #4facfe;
  border: 1px solid rgba(79, 172, 254, 0.3);
  border-radius: 20px;
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
  transition: all 0.3s ease;
}

.card-cover-glassmorphism .cover-tag:hover {
  background: rgba(79, 172, 254, 0.25);
  transform: translateY(-2px);
  box-shadow: 0 4px 8px rgba(79, 172, 254, 0.2);
}

.card-cover-glassmorphism .cover-date {
  color: rgba(255, 255, 255, 0.7);
}

/* 特殊元素：进度条 */
.card-glassmorphism .progress-bar {
  width: 100%;
  height: 6px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 3px;
  margin: 20px 0;
  overflow: hidden;
  position: relative;
}

.card-glassmorphism .progress-bar:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 70%; /* 可调整进度 */
  background: linear-gradient(90deg, #4facfe, #00f2fe);
  border-radius: 3px;
}
