  z-index: 2;
}

.card-japanese-magazine .card-content {
  position: relative;
  background: #ffffff;
  height: calc(var(--card-height) - 100px);
}

/* 主标题样式 - 日系杂志风格 */
.card-japanese-magazine h1 {
  font-family:
    "Helvetica Neue", "YuGothic", "Hiragino Kaku Gothic ProN", sans-serif;
  font-size: 30px;
  position: relative;
  color: #000;
  margin: 25px 0 25px 0;
  letter-spacing: -1px;
  font-weight: 800;
  text-align: left;
  line-height: 1.1;
  padding-left: 15px;
  padding-bottom: 0;
  border-left: 5px solid #ff0033;
}

/* 标题装饰线 */
.card-japanese-magazine h1:after {
  content: "";
  display: block;
  width: 40px;
  height: 3px;
  background: #000;
}

/* 副标题样式 */
.card-japanese-magazine h2 {
  font-size: 18px;
  color: #ff0033;
  margin: 25px 0 15px 0;
  font-weight: 700;
  letter-spacing: 1px;
  text-align: left;
  position: relative;
  display: inline-block;
  padding: 0 5px;
  background: #fff;
  z-index: 2;
}

/* 副标题背景装饰 */
.card-japanese-magazine h2::after {
  content: "";
  display: block;
  height: 4px;
  width: 100%;
  background: #ddd;
  transform: translate3d(0px, -2px, 0px);
}

/* 段落样式 */
.card-japanese-magazine p {
  color: #333;
}

.card-japanese-magazine strong {
  color: #000;
  font-weight: 700;
  background: linear-gradient(transparent 60%, rgba(255, 0, 51, 0.2) 40%);
  padding: 0 2px;
}

.card-japanese-magazine em {
  color: #ff0033;
  font-style: normal;
  font-weight: 600;
}

.card-japanese-magazine p a {
  color: #000;
  text-decoration: none;
  border-bottom: 1px solid #ff0033;
  transition: all 0.2s ease;
}

.card-japanese-magazine p a:hover {
  color: #ff0033;
}

/* 有序列表样式 */

.card-japanese-magazine li {
  counter-increment: item;
  list-style: none;
  margin-bottom: 15px;
  color: #333;
  line-height: 1.6;
  font-size: 16px;
}

/* 序号日系杂志风格 */
.card-japanese-magazine li:before {
  content: attr(data-index);
  width: 26px;
  height: 26px;
  display: block;
  transform: translate(-8px, 0);
  float: left;
  background: #000;
  color: #fff;
  text-align: center;
  line-height: 26px;
  font-weight: bold;
  font-size: 14px;
}

/* 小标题样式 */
.card-japanese-magazine h3 {
  display: block;
  color: #000;
  margin: 20px 0 10px 0;
  font-size: 16px;
  font-weight: 700;
  letter-spacing: 0.5px;
}

/* 表格样式 */
.card-japanese-magazine table {
  border-collapse: collapse;
  margin: 20px 0;
  width: 100%;
}

.card-japanese-magazine th {
  background-color: #000;
  color: #fff;
  padding: 10px;
  text-align: left;
  font-weight: 600;

  font-size: 12px;
  letter-spacing: 1px;
}

.card-japanese-magazine td {
  color: #333;
  padding: 10px;
  border-bottom: 1px solid #eee;
}

.card-japanese-magazine tr:nth-child(even) {
  background-color: #f9f9f9;
}

/* 代码块样式 */
.card-japanese-magazine pre {
  border: 1px solid #eee;
  background-color: #f9f9f9;
  padding: 15px;
  overflow-x: auto;
  font-family: "SF Mono", monospace;
  font-size: 14px;
}

.card-japanese-magazine code:not(pre code) {
  background-color: #f1f1f1;
  color: #ff0033;
  padding: 2px 4px;
  font-family: "SF Mono", monospace;
  font-size: 0.9em;
}

/* 引用块样式 */
.card-japanese-magazine blockquote {
  background-color: #fff;
  border-left: 3px solid #ff0033;
  color: #666;
  padding: 15px 20px;
  margin: 20px 0;
  font-style: italic;
  position: relative;
}

.card-japanese-magazine blockquote:before {
  content: "“";
  display: block;
  height: 1px;
  transform: translate(0, -24px);
  color: rgba(255, 0, 51, 0.2);
  font-size: 40px;
  font-family: serif;
}

.card-japanese-magazine ul > li:before {
  content: "";
  display: block;
  float: left;
  transform: translate(-8px, 8px);
  width: 8px;
  height: 8px;
  color: #ff0033;
  font-size: 12px;
}

/* 水平线样式 */
.card-japanese-magazine hr {
  height: 3px;
  border: none;
  background: repeating-linear-gradient(
    90deg,
    #000,
    #000 5px,
    transparent 5px,
    transparent 10px
  );
  margin: 25px 0;
}

/* 图片样式 */
.card-japanese-magazine img {
  max-width: 100%;
  height: auto;
  margin: 20px 0;
  border: 5px solid #fff;
  box-shadow: 0 3px 10px rgba(0, 0, 0, 0.1);
  transform: rotate(-1deg);
}

/* 添加日系杂志风格的页脚装饰 */
.card-japanese-magazine .card-footer {
  text-align: right;
  font-size: 12px;
  color: #333;

  letter-spacing: 1px;
  border-top: 1px solid #eee;
  padding-top: 15px;
}

/* 添加随机倾斜效果的类 */
.card-japanese-magazine .tilt-left {
  transform: rotate(-2deg);
}

.card-japanese-magazine .tilt-right {
  transform: rotate(1deg);
}

/* 添加日系杂志特有的装饰元素 */
.card-japanese-magazine .card-header:before {
  content: "STYLE";
  position: absolute;
  top: 15px;
  right: 20px;
  font-size: 10px;
  color: #fff;
  background: #000;
  padding: 3px 8px;
  letter-spacing: 1px;
  font-weight: bold;
  transform: rotate(3deg);
  z-index: 3;
}

/* 添加日系风格的下划线装饰 */
.card-japanese-magazine .highlight {
  background: linear-gradient(transparent 60%, rgba(255, 0, 51, 0.3) 40%);
  padding: 0 2px;
}
