}

/* 添加警告区块 */
.card-cyberpunk .warning-block {
  background: rgba(0, 0, 0, 0.3);
  border-left: 3px solid #ff0080;
  padding: 15px;
  margin: 20px 0;
  position: relative;
}

.card-cyberpunk .warning-block:before {
  content: "!";
  position: absolute;
  top: 15px;
  left: -15px;
  width: 25px;
  height: 25px;
  background: #ff0080;
  color: #000;
  border-radius: 50%;
  text-align: center;
  line-height: 25px;
  font-weight: bold;
  font-family: "Orbitron", sans-serif;
}

/* 添加卡片头部装饰 */
.card-cyberpunk .card-header {
  position: relative;
  height: 5px;
  background: linear-gradient(90deg, #ff0080, #00ffff);
  margin-bottom: 20px;
  flex: 0 0 auto;
}

.card-cyberpunk .card-header:before {
  content: "CYBER";
  position: absolute;
  top: 10px;
  right: 0;
  font-size: 10px;
  color: #00ffff;
  font-family: "Orbitron", sans-serif;
  letter-spacing: 1px;
  text-shadow: 0 0 5px rgba(0, 255, 255, 0.7);
}

/* 添加卡片底部装饰 */
.card-cyberpunk .card-footer {
  position: relative;
  margin-top: 30px;
  padding-top: 15px;
  border-top: 1px solid rgba(0, 255, 255, 0.3);
  font-size: 10px;
  color: rgba(0, 255, 255, 0.7);
  font-family: "Orbitron", sans-serif;

  letter-spacing: 1px;
  text-align: right;
}

.card-cyberpunk .card-footer:after {
  content: "// END TRANSMISSION";
  position: absolute;
  bottom: 0px;
  left: 0;
  font-size: 8px;
  color: rgba(255, 0, 128, 0.7);
}

/* 添加进度条效果 */
.card-cyberpunk .progress-bar {
  width: 100%;
  height: 10px;
  background: rgba(0, 0, 0, 0.3);
  margin: 15px 0;
  position: relative;
  overflow: hidden;
}

.card-cyberpunk .progress-bar:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 60%;
  background: linear-gradient(
    90deg,
    rgba(0, 255, 255, 0.7),
    rgba(0, 255, 255, 0.3)
  );
  /* animation: progress 2s infinite linear; */
}

@keyframes progress {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

/* 添加全局故障动画效果 */
.card-cyberpunk {
  animation: card-glitch 10s infinite;
}

@keyframes card-glitch {
  0%,
  100% {
    filter: none;
  }
  92% {
    filter: none;
  }
  92.5% {
    filter: hue-rotate(90deg) brightness(1.1);
  }
  93% {
    filter: none;
  }
  93.5% {
    filter: brightness(1.2) contrast(1.2);
  }
  94% {
    filter: none;
  }
  98% {
    filter: none;
  }
  98.2% {
    filter: brightness(1.2) contrast(1.3) hue-rotate(-20deg);
  }
  98.4% {
    filter: none;
  }
}

/* 添加数据标记效果 */
.card-cyberpunk mark {
  position: relative;
  background: linear-gradient(
    transparent 20%,
    rgba(0, 255, 255, 0.2) 20%,
    rgba(0, 255, 255, 0.2) 80%,
    transparent 80%
  );
  padding: 0 3px;
  color: #00ffff;
}

.card-cyberpunk mark::after {
  display: none;
}
.card-cyberpunk mark:before {
  content: "ALERT";
  position: absolute;
  top: -15px;
  left: 0;
  font-size: 8px;
  color: rgba(0, 255, 255, 0.7);
  font-family: "Rajdhani", sans-serif;
}

.card-cyberpunk .card-footer {
  text-align: right;
}

.card-cyberpunk .card-footer .page {
  font-size: 12px;
}
