  letter-spacing: 1px;
  border-top: 1px solid #eee;
  padding-top: 15px;
}

/* 添加随机倾斜效果的类 */
.card-japanese-magazine .tilt-left {
  transform: rotate(-2deg);
}

.card-japanese-magazine .tilt-right {
  transform: rotate(1deg);
}

/* 添加日系杂志特有的装饰元素 */
.card-japanese-magazine .card-header:before {
  content: "STYLE";
  position: absolute;
  top: 15px;
  right: 20px;
  font-size: 10px;
  color: #fff;
  background: #000;
  padding: 3px 8px;
  letter-spacing: 1px;
  font-weight: bold;
  transform: rotate(3deg);
  z-index: 3;
}

