/* 纸张边缘效果 */
.card-typewriter:after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 5px;
  background-image: linear-gradient(
    90deg,
    transparent 0%,
    rgba(0, 0, 0, 0.05) 50%,
    transparent 100%
  );
}

.card-typewriter .card-content {
  position: relative;
  z-index: 1;
  background: rgba(248, 243, 227, 0.7);
  border: 1px solid rgba(211, 198, 166, 0.3);
  padding: 20px;
  border-radius: 2px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
  height: 100%;
}

.card-typewriter .card-content-inner {
  position: relative;
  z-index: 1;
}

/* 主标题样式 */
.card-typewriter h1 {
  font-size: 24px;
  color: #3a3a3a;
  margin-bottom: 15px;
  letter-spacing: -0.5px;
  font-weight: 600;
  text-align: center;
  font-family: "Courier New", monospace;
  position: relative;
  padding-bottom: 10px;
}

.card-typewriter h1:after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 100px;
  height: 2px;
  background: rgba(58, 58, 58, 0.2);
}

/* 副标题样式 */
.card-typewriter h2 {
  font-size: 16px;
  color: #5a5a5a;
  margin-bottom: 24px;
  font-weight: 400;
  letter-spacing: -0.5px;
  text-align: center;
  font-style: italic;
  font-family: "Courier New", monospace;
}

/* 段落样式 */
.card-typewriter p {
  color: #3a3a3a;
  font-size: 15px;
  position: relative;
  font-family: "Courier New", monospace;
}

.card-typewriter strong {
  color: #000;
  font-weight: 600;
}

.card-typewriter em {
  color: #5a5a5a;
  font-style: italic;
}

.card-typewriter p a {
  color: #8b4513;
  text-decoration: none;
  border-bottom: 1px dashed #8b4513;
  transition: all 0.3s ease;
  position: relative;
}

.card-typewriter p a:hover {
  color: #5e2f0d;
  border-bottom: 1px solid #5e2f0d;
}

/* 有序列表样式 */

.card-typewriter li {
  position: relative;
  counter-increment: item;
  list-style: none;
  margin-bottom: 16px;
  padding-left: 40px;
  color: #3a3a3a;
  line-height: 1.6;
  font-size: 15px;
  font-family: "Courier New", monospace;
  letter-spacing: -0.3px;
}

/* 序号样式 */
.card-typewriter li:before {
  content: attr(data-index);
  position: absolute;
  left: 0;
  top: -2px;
  width: 28px;
  height: 28px;
  background: rgba(211, 198, 166, 0.3);
  border: 1px solid rgba(139, 69, 19, 0.3);
  color: #8b4513;
  text-align: center;
  line-height: 26px;
  font-weight: bold;
  border-radius: 50%;
}

/* 小标题样式 */
.card-typewriter h3 {
  display: block;
  color: #3a3a3a;
  margin-bottom: 6px;
  font-size: 16px;
  font-weight: 600;
  letter-spacing: -0.5px;
  text-decoration: underline;
  text-decoration-color: rgba(139, 69, 19, 0.3);
  text-underline-offset: 3px;
}

/* 表格样式 */
.card-typewriter table {
  width: 100%;
  border-collapse: collapse;
  margin: 20px 0;
  background: rgba(248, 243, 227, 0.5);
  border: 1px solid #d3c6a6;
}

.card-typewriter th {
  background: rgba(211, 198, 166, 0.2);
  color: #3a3a3a;
  border-bottom: 1px solid #d3c6a6;
  padding: 10px;
  font-weight: 600;
  text-align: left;
  letter-spacing: -0.5px;
}

.card-typewriter td {
  color: #3a3a3a;
  padding: 10px;
  border-bottom: 1px solid rgba(211, 198, 166, 0.5);
  letter-spacing: -0.3px;
}

.card-typewriter tr:nth-child(even) {
  background: rgba(211, 198, 166, 0.1);
}

/* 代码块样式 */
.card-typewriter pre {
  background: rgba(211, 198, 166, 0.2);
  border: 1px solid #d3c6a6;
  border-radius: 2px;
  padding: 15px;
  overflow-x: auto;
  position: relative;
  font-family: "Courier New", monospace;
}

.card-typewriter code:not(pre code) {
  background: rgba(211, 198, 166, 0.2);
  color: #8b4513;
  padding: 2px 5px;
  border-radius: 2px;
  font-family: "Courier New", monospace;
  font-size: 0.9em;
  border: 1px solid rgba(211, 198, 166, 0.5);
}

/* 引用块样式 */
.card-typewriter blockquote {
  background: rgba(211, 198, 166, 0.1);
  border-left: 3px solid #d3c6a6;
  color: #5a5a5a;
  padding: 15px 20px;
  margin: 20px 0;
  font-style: italic;
  position: relative;
  border-radius: 0 2px 2px 0;
}

.card-typewriter blockquote:before {
  content: "“";
  position: absolute;
  left: 5px;
  top: 5px;
  color: rgba(139, 69, 19, 0.3);
  font-size: 24px;
  font-family: Georgia, serif;
}

.card-content ol,
.card-content ul {
  padding-left: 0;
}
.card-typewriter ul > li {
  position: relative;
  list-style: none;
  margin-bottom: 12px;
  padding-left: 40px;
  line-height: 1.6;
}

.card-typewriter ul > li:before {
  content: "•";
  position: absolute;
  left: 0;
  top: -5px;
  color: #8b4513;
  font-weight: bold;
  font-size: 18px;
}

/* 水平线样式 */
.card-typewriter hr {
  border: 0;
  height: 1px;
  background: repeating-linear-gradient(
    90deg,
    transparent,
    transparent 5px,
    #d3c6a6 5px,
    #d3c6a6 10px
  );
  margin: 25px 0;
  opacity: 0.7;
}

/* 图片样式 */
.card-typewriter img {
  max-width: 100%;
  border: 1px solid #d3c6a6;
  border-radius: 2px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  margin: 20px 0;
  padding: 5px;
  background: #fff;
}

/* 打字机笔记效果 */
.card-typewriter .typed-note {
  background: rgba(211, 198, 166, 0.2);
  border: 1px solid #d3c6a6;
  border-radius: 2px;
  padding: 15px;
  margin: 20px 0;
  position: relative;
  font-family: "Courier New", monospace;
  color: #3a3a3a;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.05);
}

.card-typewriter .typed-note:before {
  content: "NOTE";
  position: absolute;
  top: -8px;
  left: 10px;
  background: #f8f3e3;
  color: #8b4513;
  font-size: 10px;
  padding: 0 5px;
  border: 1px solid #d3c6a6;
}

.card-typewriter .card-footer {
  text-align: right;
}

.card-typewriter .card-footer .page {
  font-size: 12px;
}
