    linear-gradient(90deg, rgba(18, 16, 36, 0.8) 1px, transparent 1px);
  background-size: 20px 20px;
  z-index: 1;
  opacity: 0.2;
}

/* 故障艺术装饰 */
.card-cyberpunk:after {
  content: "";
  position: absolute;
  top: 0;
  left: -10%;
  width: 120%;
  height: 10px;
  background: linear-gradient(
    90deg,
    transparent 5%,
    rgba(0, 255, 255, 0.8) 15%,
    rgba(255, 0, 128, 0.8) 25%,
    transparent 35%
  );
  opacity: 0.7;
  filter: blur(1px);
  transform: skewX(-20deg);
