  background: rgba(0, 255, 255, 0.5);
  animation: link-glitch 2s infinite;
}

@keyframes link-glitch {
  0% {
    transform: translateX(0);
  }
  80% {
    transform: translateX(0);
  }
  85% {
    transform: translateX(-3px);
  }
  90% {
    transform: translateX(3px);
  }
  95% {
    transform: translateX(-2px);
  }
  100% {
    transform: translateX(0);
  }
