  background: #fff;
  padding: 20px;
  height: 100%;
  border-radius: 10px;
}

/* 主标题样式 - 水彩晕染效果 */
.card-watercolor h1 {
  font-family: "Ma Shan Zheng", "PingFang SC", cursive;
  font-size: 32px;
  position: relative;
  color: #333;
  margin-bottom: 20px;
  font-weight: 500;
  text-align: center;
  padding: 10px 0;
}

/* 标题水彩晕染效果 */
.card-watercolor h1:before {
  content: attr(data-text);
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    45deg,
    rgba(173, 216, 230, 0.4),
    rgba(221, 160, 221, 0.4),
    rgba(255, 182, 193, 0.4)
  );
  -webkit-background-clip: text;
  background-clip: text;
  color: transparent;
  z-index: -1;
  filter: blur(3px);
}

/* 标题下方水彩线条 */
.card-watercolor h1:after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 120px;
  height: 3px;
  background: linear-gradient(
    to right,
    transparent,
    rgba(173, 216, 230, 0.7),
    rgba(221, 160, 221, 0.7),
    rgba(255, 182, 193, 0.7),
    transparent
  );
}

/* 副标题样式 */
.card-watercolor h2 {
  font-size: 18px;
  color: #555;
  margin-bottom: 24px;
  font-weight: 400;
  text-align: center;
  position: relative;
  padding-bottom: 15px;
  font-family: "Xingkai SC", "STKaiti", serif;
}

.card-watercolor h2:after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 60px;
  height: 2px;
  background: linear-gradient(
    to right,
    rgba(173, 216, 230, 0.5),
    rgba(221, 160, 221, 0.5)
  );
}

/* 段落样式 */
.card-watercolor p,
.card-watercolor li {
  color: #444;
  font-size: 16px;
  line-height: 1.8;
  margin: 16px 0;
  font-family: "STKaiti", "PingFang SC", sans-serif;
}

.card-watercolor strong {
  color: #333;
  font-weight: 600;
}

.card-watercolor em {
  color: #9370db;
  font-style: italic;
}

.card-watercolor p a {
  color: #9370db;
  text-decoration: none;
  border-bottom: 1px dashed rgba(147, 112, 219, 0.5);
  transition: all 0.3s ease;
}

.card-watercolor p a:hover {
  color: #7b68ee;
  border-bottom: 1px solid rgba(147, 112, 219, 0.8);
}

/* 有序列表样式 */
.card-watercolor ol > li {
  position: relative;
  counter-increment: item;
  list-style: none;
  margin-bottom: 20px;
  padding-left: 30px;
  color: #444;
  font-size: 16px;
  font-family: "STKaiti", "PingFang SC", sans-serif;
}

/* 序号水彩效果 */
.card-watercolor li:before {
  content: attr(data-index);
  position: absolute;
  left: 0;
  top: 0;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  background: linear-gradient(
    135deg,
    rgba(173, 216, 230, 0.4),
    rgba(221, 160, 221, 0.4)
  );
  color: #444;
  text-align: center;
  line-height: 24px;
  font-size: 14px;
  font-weight: bold;
  box-shadow: 0 0 5px rgba(173, 216, 230, 0.3);
}

/* 小标题样式 */
.card-watercolor h3 {
  display: block;
  color: #333;
  margin-bottom: 6px;
  font-size: 17px;
  font-weight: 600;
  font-family: "Xingkai SC", "STKaiti", serif;
}

/* 表格样式 */
.card-watercolor table {
  box-shadow: 0 4px 15px rgba(173, 216, 230, 0.2);
  border-radius: 8px;
  overflow: hidden;
  border-collapse: collapse;
  margin: 20px 0;
}

.card-watercolor th {
  background-color: rgba(173, 216, 230, 0.2);
  color: #333;
  border-bottom: 2px solid rgba(173, 216, 230, 0.4);
  padding: 10px 15px;
  text-align: left;
}

.card-watercolor td {
  color: #444;
  padding: 10px 15px;
  border-bottom: 1px solid rgba(173, 216, 230, 0.2);
}

.card-watercolor tr:nth-child(even) {
  background-color: rgba(248, 248, 255, 0.5);
}

/* 代码块样式 */
.card-watercolor pre {
  border: 1px solid rgba(173, 216, 230, 0.4);
  box-shadow: 0 4px 15px rgba(173, 216, 230, 0.1);
  background-color: rgba(248, 248, 255, 0.7);
  border-radius: 8px;
  padding: 15px;
  overflow-x: auto;
}

.card-watercolor code:not(pre code) {
  background-color: rgba(173, 216, 230, 0.2);
  color: #9370db;
  padding: 2px 5px;
  border-radius: 4px;
  font-family: monospace;
}

/* 引用块样式 */
.card-watercolor blockquote {
  background-color: rgba(248, 248, 255, 0.7);
  border-left: 4px solid rgba(221, 160, 221, 0.6);
  color: #666;
  padding: 15px 20px;
  margin: 20px 0;
  border-radius: 0 8px 8px 0;
  font-style: italic;
}

.card-content ol,
.card-content ul {
  padding-left: 0;
}
.card-watercolor ul > li {
  position: relative;
  list-style: none;
  margin-bottom: 16px;
  padding-left: 30px;
  font-family: "STKaiti", "PingFang SC", sans-serif;
}

.card-watercolor ul > li:before {
  content: "❀";
  position: absolute;
  left: 0;
  top: 0;
  width: 24px;
  height: 24px;
  line-height: 24px;
  color: rgba(221, 160, 221, 0.8);
  font-size: 16px;
}

/* 水平线样式 */
.card-watercolor hr {
  height: 2px;
  border: none;
  background: linear-gradient(
    to right,
    transparent,
    rgba(173, 216, 230, 0.5),
    rgba(221, 160, 221, 0.5),
    rgba(255, 182, 193, 0.5),
    transparent
  );
  margin: 25px 0;
}

/* 图片样式 */
.card-watercolor img {
  border: 3px solid rgba(255, 255, 255, 0.8);
  box-shadow: 0 5px 15px rgba(173, 216, 230, 0.3);
  border-radius: 8px;
