}

.card-cyberpunk .progress-bar:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 60%;
  background: linear-gradient(
    90deg,
    rgba(0, 255, 255, 0.7),
    rgba(0, 255, 255, 0.3)
  );
