  font-size: 0.9em;
  border: 1px solid rgba(0, 255, 255, 0.2);
}

/* 引用块样式 */
.card-cyberpunk blockquote {
  background-color: rgba(255, 0, 128, 0.1);
  border-left: 3px solid rgba(255, 0, 128, 0.5);
  color: #e0e0e0;
  padding: 15px 20px;
  margin: 20px 0;
  font-style: italic;
  position: relative;
}

.card-cyberpunk blockquote:before {
  content: ">";
  position: absolute;
  top: 5px;
  left: 5px;
  color: rgba(255, 0, 128, 0.3);
  font-size: 24px;
  font-family: monospace;
}

.card-content ol,
.card-content ul {
  padding-left: 0;
}
.card-cyberpunk ul > li:before {
  content: ">";
  position: absolute;
  left: 0;
  top: -3px;
  color: #00ffff;
  font-size: 14px;
  font-weight: bold;
  font-family: monospace;
}

/* 水平线样式 */
.card-cyberpunk hr {
  height: 2px;
  border: none;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(0, 255, 255, 0.5),
    rgba(255, 0, 128, 0.5),
    transparent
  );
  margin: 25px 0;
}

/* 图片样式 */
.card-cyberpunk img {
  max-width: 100%;
  height: auto;
  margin: 20px 0;
  border: 1px solid rgba(0, 255, 255, 0.3);
  box-shadow: 0 0 15px rgba(0, 255, 255, 0.2);
  position: relative;
  filter: contrast(1.1) brightness(1.1);
}

/* 图片故障效果 */
.card-cyberpunk img:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    45deg,
