/* 段落样式 */
.card-cyberpunk p {
  color: #e0e0e0;
  font-size: 15px;
  text-align: justify;
  font-family: "Rajdhani", "PingFang SC", sans-serif;
}

.card-cyberpunk strong {
  color: #ff0080;
  font-weight: 700;
  text-shadow: 0 0 3px rgba(255, 0, 128, 0.5);
}

.card-cyberpunk em {
  color: #00ffff;
  font-style: italic;
  text-shadow: 0 0 3px rgba(0, 255, 255, 0.5);
}

.card-cyberpunk p a {
  color: #00ffff;
  text-decoration: none;
  border-bottom: 1px solid rgba(0, 255, 255, 0.3);
  transition: all 0.2s ease;
  position: relative;
  padding: 0 3px;
}

.card-cyberpunk p a:hover {
  background: rgba(0, 255, 255, 0.1);
  border-bottom: 1px solid rgba(0, 255, 255, 0.7);
}

.card-cyberpunk p a:before {
  content: "";
  position: absolute;
  bottom: -2px;
  left: 0;
  width: 100%;
  height: 1px;
  background: rgba(0, 255, 255, 0.5);
  animation: link-glitch 2s infinite;
}

@keyframes link-glitch {
  0% {
    transform: translateX(0);
  }
  80% {
    transform: translateX(0);
  }
  85% {
    transform: translateX(-3px);
  }
  90% {
    transform: translateX(3px);
  }
  95% {
    transform: translateX(-2px);
  }
  100% {
    transform: translateX(0);
  }
}

/* 有序列表样式 */
.card-cyberpunk li {
  position: relative;
  counter-increment: item;
  list-style: none;
  margin-bottom: 15px;
  padding-left: 40px;
  color: #e0e0e0;
  line-height: 1.6;
  font-size: 15px;
  font-family: "Rajdhani", "PingFang SC", sans-serif;
}

/* 序号赛博朋克风格 */
.card-cyberpunk li:before {
  content: attr(data-index);
  position: absolute;
  left: 0;
  top: -3px;
  width: 28px;
  height: 28px;
  background: rgba(255, 0, 128, 0.2);
  color: #ff0080;
  text-align: center;
  line-height: 28px;
  font-weight: bold;
  font-size: 14px;
  border: 1px solid rgba(255, 0, 128, 0.5);
  box-shadow: 0 0 5px rgba(255, 0, 128, 0.3);
}

/* 小标题样式 */
