}

/* 添加数据面板效果 */
.card-cyberpunk .data-panel {
  background: rgba(13, 14, 25, 0.8);
  border: 1px solid rgba(0, 255, 255, 0.3);
  padding: 15px;
  margin: 20px 0;
  position: relative;
}

.card-cyberpunk .data-panel:before {
  content: "DATA";
  position: absolute;
  top: -10px;
  left: 10px;
  background: #0d0e19;
  color: #00ffff;
  font-size: 12px;
  padding: 0 5px;
  font-family: "Orbitron", sans-serif;
}

/* 添加扫描线效果 */
.card-cyberpunk .card-content:after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    to bottom,
    transparent 50%,
    rgba(0, 0, 0, 0.05) 50%
  );
