}

/* 暗黑模式变量 */
.dark-mode {
  --notes-bg: #1c1c1e;
  --notes-text: #ffffff;
  --notes-title-color: rgb(255, 214, 10);
  --notes-accent: #ff453a;
  --notes-border: rgba(255, 255, 255, 0.1);
  --notes-quote-color: #989898;
  --notes-code-bg: rgba(255, 255, 255, 0.1);
  --notes-mark-bg: rgba(255, 214, 10, 0.3);
  --notes-table-header: rgba(255, 214, 10, 0.2);
  --notes-table-border: rgba(255, 255, 255, 0.1);
}

